cssCode:
.btn{
  display:inline-block; padding:.7rem 1.2rem; border-radius:.6rem; font-weight:600;
  background:#111; color:#fff; transition:transform .2s ease, box-shadow .2s ease;
}
.btn.glow:hover{
  transform:translateY(-2px);
  box-shadow:0 10px 20px rgba(17,17,17,.25), 0 0 0 4px rgba(17,17,17,.1) inset;
}
