cssCode:
.btn{
  display: inline-block;
  padding: .6rem 1rem;
  font-weight: 600;
  color: #111;
  text-decoration: none;
}

.btn.uline{
  position: relative;
}
.btn.uline::after{
  content: '';
  position: absolute;
  left: 0;
  bottom: -2px;
  width: 100%;
  height: 2px;
  background: #111;
  transform: scaleX(0);
  transform-origin: left;
  transition: transform .25s ease;
}
.btn.uline:hover::after,
.btn.uline:focus-visible::after{
  transform: scaleX(1);
}

/* アクセシビリティ: キーボードフォーカスの可視化 */
.btn.uline:focus-visible{
  outline: 2px solid #111;
  outline-offset: 2px;
}
